mapper.update:
update db_account
type = #{user.type}, sex = #{user.sex}, username = #{user.username}, password = #{user.password}, email = #{user.email}, nickname = #{user.nickname}, birthday = #{user.birthday},
where id = #{user.id}

mapper.select:
SELECT * FROM db_account


        AND id > #{userId}

    AND type = #{user.type}
    AND sex = #{user.sex}

        AND (username LIKE CONCAT('%', #{keyword}, '%') OR id = #{keyword})
